package com.xworkz.inheritance.external;

public class Clothing {
    public void clothing() {
        System.out.println("no-args constructor of Clothing");
    }

    public void covering() {
        System.out.println("Clothing is used for covering the body");
    }

    public void protecting() {
        System.out.println("Clothing provides protection");
    }

    public void fashion() {
        System.out.println("Clothing represents fashion");
    }

    public void comfort() {
        System.out.println("Clothing provides comfort");
    }

    public void identity() {
        System.out.println("Clothing signifies identity");
    }
}
