package com.xworks.Inheritance.external;

public class Aircraft {
    public void fly() {
        System.out.println("Aircraft is flying");
    }

    public void land() {
        System.out.println("Aircraft is landing");
    }

    public void refuel() {
        System.out.println("Aircraft is refueling");
    }

    public void hover() {
        System.out.println("Aircraft is hovering");
    }

    public void loadPassengers() {
        System.out.println("Aircraft is loading passengers");
    }
}
