package com.xworks.Inheritance.external;

public class Antibiotic {
    public void killBacteria() {
        System.out.println("Antibiotic is killing bacteria.");
    }

    public void treatInfections() {
        System.out.println("Antibiotic is treating infections.");
    }

    public void reduceFever() {
        System.out.println("Antibiotic is reducing fever.");
    }

    public void increaseImmunity() {
        System.out.println("Antibiotic is increasing immunity.");
    }

    public void manageDosage() {
        System.out.println("Antibiotic is managing dosage.");
    }
}
