package com.xworks.Inheritance.external;

public class Appliance {
    public void wash() {
        System.out.println("Appliance is washing");
    }

    public void rinse() {
        System.out.println("Appliance is rinsing");
    }

    public void spin() {
        System.out.println("Appliance is spinning");
    }

    public void drain() {
        System.out.println("Appliance is draining");
    }

    public void beep() {
        System.out.println("Appliance is beeping");
    }
}
