package com.xworks.Inheritance.external;

public class BoardGame {
    public void setupBoard() {
        System.out.println("BoardGame: Setting up the board...");
    }

    public void startGame() {
        System.out.println("BoardGame: Starting the game...");
    }

    public void makeMove() {
        System.out.println("BoardGame: Making a move...");
    }

    public void checkWinCondition() {
        System.out.println("BoardGame: Checking win condition...");
    }

    public void endGame() {
        System.out.println("BoardGame: Ending the game...");
    }
}
