package com.xworks.Inheritance.external;

public class Building {
    public void provideShelter() {
        System.out.println("Building provides shelter.");
    }

    public void lockDoors() {
        System.out.println("Building locks doors.");
    }

    public void openWindows() {
        System.out.println("Building opens windows.");
    }

    public void haveRooms() {
        System.out.println("Building has rooms.");
    }

    public void insulate() {
        System.out.println("Building provides insulation.");
    }
}
