package com.xworks.Inheritance.external;

public class Capture {
    public void takePhoto() {
        System.out.println("Capture is taking a photo.");
    }

    public void recordVideo() {
        System.out.println("Capture is recording a video.");
    }

    public void zoomIn() {
        System.out.println("Capture is zooming in.");
    }

    public void adjustBrightness() {
        System.out.println("Capture is adjusting brightness.");
    }

    public void storeImages() {
        System.out.println("Capture is storing images.");
    }
}
