package com.xworks.Inheritance.external;

public class Christmas {
    public void decorateTree() {
        System.out.println("Christmas: Decorating the tree.");
    }

    public void exchangeGifts() {
        System.out.println("Christmas: Exchanging gifts.");
    }

    public void singCarols() {
        System.out.println("Christmas: Singing carols.");
    }

    public void cookDinner() {
        System.out.println("Christmas: Cooking Christmas dinner.");
    }

    public void celebrate() {
        System.out.println("Christmas: Celebrating the holiday.");
    }
}

