package com.xworks.Inheritance.external;

public class Clock {
    public void showTime() {
        System.out.println("Clock shows the time.");
    }

    public void tick() {
        System.out.println("Clock makes ticking sound.");
    }

    public void setAlarm() {
        System.out.println("Clock alarm is set.");
    }

    public void display() {
        System.out.println("Clock display is on.");
    }

    public void stopAlarm() {
        System.out.println("Clock alarm stopped.");
    }
}
