package com.xworks.Inheritance.external;

public class Cold {
    public void freeze() {
        System.out.println("Cold item is freezing.");
    }

    public void scoop() {
        System.out.println("Cold item is being scooped.");
    }

    public void melt() {
        System.out.println("Cold item is melting.");
    }

    public void flavorMix() {
        System.out.println("Cold item is mixing flavors.");
    }

    public void eat() {
        System.out.println("Cold item is being eaten.");
    }
}
