package com.xworks.Inheritance.external;

public class Colour {
    public void displayColour() {
        System.out.println("Displaying Colour");
    }

    public void mixColours() {
        System.out.println("Mixing Colours");
    }

    public void paint() {
        System.out.println("Painting with Colour");
    }

    public void dry() {
        System.out.println("Colour is drying");
    }

    public void fade() {
        System.out.println("Colour is fading over time");
    }
}
