package com.xworks.Inheritance.external;

public class Controller {
    public void accelerate() {
        System.out.println("Controller: Accelerating...");
    }

    public void brake() {
        System.out.println("Controller: Braking...");
    }

    public void turnLeft() {
        System.out.println("Controller: Turning Left...");
    }

    public void turnRight() {
        System.out.println("Controller: Turning Right...");
    }

    public void stop() {
        System.out.println("Controller: Stopping...");
    }

}
