package com.xworks.Inheritance.external;

public class Country {
    public void nationalAnthem() {
        System.out.println("Country has a national anthem.");
    }

    public void nationalAnimal() {
        System.out.println("Country has a national animal.");
    }

    public void nationalLanguage() {
        System.out.println("Country has a national language.");
    }

    public void nationalSport() {
        System.out.println("Country has a national sport.");
    }

    public void nationalFlag() {
        System.out.println("Country has a national flag.");
    }
}
