package com.xworks.Inheritance.external;

public class Course {
    public void enroll() {
        System.out.println("Enrolling in the course...");
    }

    public void startCourse() {
        System.out.println("Starting the course...");
    }

    public void completeCourse() {
        System.out.println("Completing the course...");
    }

    public void getCertificate() {
        System.out.println("Receiving course certificate...");
    }

    public void reviewCourse() {
        System.out.println("Reviewing the course...");
    }
}
