package com.xworks.Inheritance.external;

public class Currency {
    public void exchange() {
        System.out.println("Currency is exchanged.");
    }

    public void storeValue() {
        System.out.println("Currency stores value.");
    }

    public void facilitateTrade() {
        System.out.println("Currency facilitates trade.");
    }

    public void measureValue() {
        System.out.println("Currency measures value.");
    }

    public void serveAsUnit() {
        System.out.println("Currency serves as a unit of account.");
    }
}
