package com.xworks.Inheritance.external;

public class Doctor {
    public Doctor() {
        System.out.println("No-args constructor of Doctor");
    }

    public void treatingPatients() {
        System.out.println("Doctor is treating patients");
    }

    public void diagnosing() {
        System.out.println("Doctor is diagnosing a patient");
    }

    public void prescribingMedicine() {
        System.out.println("Doctor is prescribing medicine");
    }

    public void consulting() {
        System.out.println("Doctor is consulting a patient");
    }

    public void maintainingRecords() {
        System.out.println("Doctor is maintaining medical records");
    }
}
