package com.xworks.Inheritance.external;

public class Drink {
    public void brew() {
        System.out.println("Drink is being brewed");
    }

    public void pour() {
        System.out.println("Drink is being poured");
    }

    public void addMilk() {
        System.out.println("Milk is added to the drink");
    }

    public void sweeten() {
        System.out.println("Drink is sweetened");
    }

    public void drink() {
        System.out.println("Drink is being consumed");
    }
}
