package com.xworks.Inheritance.external;

public class Driver {
    public void drive() {
        System.out.println("Driver is driving.");
    }

    public void navigate() {
        System.out.println("Driver is navigating.");
    }

    public void pickUpPassenger() {
        System.out.println("Driver is picking up a passenger.");
    }

    public void dropOffPassenger() {
        System.out.println("Driver is dropping off a passenger.");
    }

    public void calculateFare() {
        System.out.println("Driver is calculating fare.");
    }
}
