package com.xworks.Inheritance.external;

public class Food {
    public void bake() {
        System.out.println("Food is being baked");
    }

    public void addToppings() {
        System.out.println("Adding toppings to food");
    }

    public void slice() {
        System.out.println("Slicing the food");
    }

    public void serve() {
        System.out.println("Serving the food");
    }

    public void eat() {
        System.out.println("Eating the food");
    }
}
