package com.xworks.Inheritance.external;

public class Footballer {
    public void train() {
        System.out.println("Footballer is training");
    }

    public void run() {
        System.out.println("Footballer is running");
    }

    public void pass() {
        System.out.println("Footballer is passing the ball");
    }

    public void shoot() {
        System.out.println("Footballer is shooting at the goal");
    }

    public void rest() {
        System.out.println("Footballer is resting after the match");
    }
}
