package com.xworks.Inheritance.external;

public class Fruit {
    public void extract() {
        System.out.println("Extracting juice from the fruit...");
    }

    public void blend() {
        System.out.println("Blending the fruit into juice...");
    }

    public void serveCold() {
        System.out.println("Serving the juice cold...");
    }

    public void addIce() {
        System.out.println("Adding ice to the juice...");
    }

    public void drink() {
        System.out.println("Drinking the juice...");
    }
}
