package com.xworks.Inheritance.external;

public class Furniture {
    public void move() {
        System.out.println("Furniture is being moved");
    }

    public void supportWeight() {
        System.out.println("Furniture is supporting weight");
    }

    public void adjustHeight() {
        System.out.println("Furniture height is being adjusted");
    }

    public void rotate() {
        System.out.println("Furniture is rotating");
    }

    public void fold() {
        System.out.println("Furniture is being folded");
    }
}
