package com.xworks.Inheritance.external;

public class Gadget {
    public void turnOn() {
        System.out.println("Gadget is turning on...");
    }

    public void turnOff() {
        System.out.println("Gadget is turning off...");
    }

    public void charge() {
        System.out.println("Gadget is charging...");
    }

    public void connectToWiFi() {
        System.out.println("Gadget is connecting to WiFi...");
    }

    public void updateSoftware() {
        System.out.println("Gadget is updating software...");
    }
}
