package com.xworks.Inheritance.external;

public class Hospital {
    public void admitPatient() {
        System.out.println("Hospital is admitting a patient.");
    }

    public void provideTreatment() {
        System.out.println("Hospital is providing treatment.");
    }

    public void dischargePatient() {
        System.out.println("Hospital is discharging a patient.");
    }

    public void maintainRecords() {
        System.out.println("Hospital is maintaining patient records.");
    }

    public void manageStaff() {
        System.out.println("Hospital is managing staff.");
    }
}
