package com.xworks.Inheritance.external;

public class House {
    public void openDoor() {
        System.out.println("House: Opening the door.");
    }

    public void closeWindow() {
        System.out.println("House: Closing the window.");
    }

    public void turnOnLights() {
        System.out.println("House: Turning on the lights.");
    }

    public void lockMainGate() {
        System.out.println("House: Locking the main gate.");
    }

    public void paintWalls() {
        System.out.println("House: Painting the walls.");
    }
}
