package com.xworks.Inheritance.external;

public class Instrument {
    public void pressKey() {
        System.out.println("Instrument: Pressing a key...");
    }

    public void sustainNote() {
        System.out.println("Instrument: Sustaining a note...");
    }

    public void playChord() {
        System.out.println("Instrument: Playing a chord...");
    }

    public void adjustVolume() {
        System.out.println("Instrument: Adjusting volume...");
    }

    public void perform() {
        System.out.println("Instrument: Performing...");
    }
}
