package com.xworks.Inheritance.external;

public class Laptop {
    public void laptop() {
        System.out.println("laptop is turning on...");
    }

    public void On() {
        System.out.println("laptop is turning off...");
    }

    public void display() {
        System.out.println("laptop is charging...");
    }

    public void specs() {
        System.out.println("laptop is connecting to WiFi...");
    }

    public void type() {
        System.out.println("laptop is updating software...");
    }
}
