package com.xworks.Inheritance.external;

public class LibraryItem {
    public void checkout() {
        System.out.println("Library item is checked out.");
    }

    public void returnItem() {
        System.out.println("Library item is returned.");
    }

    public void getInfo() {
        System.out.println("Displaying library item information.");
    }

    public void renew() {
        System.out.println("Library item is renewed.");
    }

    public void reserve() {
        System.out.println("Library item is reserved.");
    }
}
