package com.xworks.Inheritance.external;

public class LightSouce {
    public void turnOn() {
        System.out.println("LightSource is turned on.");
    }

    public void turnOff() {
        System.out.println("LightSource is turned off.");
    }

    public void adjustBrightness() {
        System.out.println("LightSource brightness is adjusted.");
    }

    public void emitLight() {
        System.out.println("LightSource is emitting light.");
    }

    public void consumeEnergy() {
        System.out.println("LightSource is consuming energy.");
    }
}
