package com.xworks.Inheritance.external;

public class Mage {
    public void mage() {
        System.out.println("No-args constructor of Mage");
    }

    public void breathing() {
        System.out.println("Mage breathing");
    }

    public void eating() {
        System.out.println("Mage eating");
    }

    public void sleeping() {
        System.out.println("Mage sleeping");
    }

    public void castingSpells() {
        System.out.println("Mage casting spells");
    }

    public void channelingMana() {
        System.out.println("Mage channeling mana");
    }
}
