package com.xworks.Inheritance.external;

public class Mobile {
    public void iPhone() {
        System.out.println("Mobile is turning on...");
    }

    public void Samsung() {
        System.out.println("Mobile is turning off...");
    }

    public void Pixel() {
        System.out.println("Mobile is charging...");
    }

    public void OnePlus() {
        System.out.println("Mobile is connecting to WiFi...");
    }

    public void Redmi() {
        System.out.println("Mobile is updating software...");
    }
}
