package com.xworks.Inheritance.external;

public class MusicalInstrument {
    public void strum() {
        System.out.println("MusicalInstrument is being strummed");
    }

    public void tune() {
        System.out.println("MusicalInstrument is being tuned");
    }

    public void pluck() {
        System.out.println("MusicalInstrument is being plucked");
    }

    public void playChord() {
        System.out.println("MusicalInstrument is playing a chord");
    }

    public void amplifySound() {
        System.out.println("MusicalInstrument is amplifying sound");
    }
}
