package com.xworks.Inheritance.external;

public class Player {
    public void play() {
        System.out.println("Player is playing.");
    }

    public void train() {
        System.out.println("Player is training.");
    }

    public void rest() {
        System.out.println("Player is resting.");
    }

    public void compete() {
        System.out.println("Player is competing.");
    }

    public void followRules() {
        System.out.println("Player is following the rules.");
    }
}
