package com.xworks.Inheritance.external;

public class RoadVehical {
    public void move() {
        System.out.println("RoadVehicle is moving.");
    }

    public void fuel() {
        System.out.println("RoadVehicle is refueling.");
    }

    public void stop() {
        System.out.println("RoadVehicle is stopping.");
    }

    public void load() {
        System.out.println("RoadVehicle is loading goods.");
    }

    public void maintain() {
        System.out.println("RoadVehicle is under maintenance.");
    }
}
