package com.xworks.Inheritance.external;

public class Schedule {
    public void startJourney() {
        System.out.println("Schedule: Journey started.");
    }

    public void reachHighSpeed() {
        System.out.println("Schedule: Train is reaching high speed.");
    }

    public void passTunnels() {
        System.out.println("Schedule: Passing through tunnels.");
    }

    public void slowDown() {
        System.out.println("Schedule: Train is slowing down.");
    }

    public void stopAtStation() {
        System.out.println("Schedule: Train is stopping at the station.");
    }
}
