package com.xworks.Inheritance.external;

public class Ship {
    public void sail() {
        System.out.println("Ship is sailing.");
    }

    public void navigate() {
        System.out.println("Ship is navigating through waters.");
    }

    public void dockAtPort() {
        System.out.println("Ship is docking at the port.");
    }

    public void provideCabins() {
        System.out.println("Ship provides cabins for passengers.");
    }

    public void hostEvents() {
        System.out.println("Ship can host onboard events.");
    }
}
