package com.xworks.Inheritance.external;

public class Shoes {
    public void wear() {
        System.out.println("Shoes are being worn.");
    }

    public void cleaning() {
        System.out.println("Shoes are being cleaned.");
    }

    public void polish() {
        System.out.println("Shoes are being polished.");
    }

    public void tieLaces() {
        System.out.println("Shoe laces are being tied.");
    }

    public void remove() {
        System.out.println("Shoes are being removed.");
    }
}
