package com.xworks.Inheritance.external;

public class Sky {
    public void emitLight() {
        System.out.println("Sky is emitting light");
    }

    public void generateHeat() {
        System.out.println("Sky is generating heat");
    }

    public void fuseHydrogen() {
        System.out.println("Sky is fusing hydrogen");
    }

    public void explodeSupernova() {
        System.out.println("Sky is exploding into a supernova");
    }

    public void collapse() {
        System.out.println("Sky is collapsing");
    }
}
