package com.xworks.Inheritance.external;

public class Snacks {
    public void openPacket() {
        System.out.println("Opening the snack packet.");
    }

    public void crunch() {
        System.out.println("Snacks are crunchy.");
    }

    public void flavor() {
        System.out.println("Snacks have different flavors.");
    }

    public void share() {
        System.out.println("Sharing snacks with friends.");
    }

    public void finish() {
        System.out.println("Finished eating snacks.");
    }
}
