package com.xworks.Inheritance.external;

public class SocialMedia {
    public void createAccount() {
        System.out.println("SocialMedia account created.");
    }

    public void login() {
        System.out.println("User logged in to SocialMedia.");
    }

    public void logout() {
        System.out.println("User logged out from SocialMedia.");
    }

    public void browseContent() {
        System.out.println("Browsing content on SocialMedia.");
    }

    public void deleteAccount() {
        System.out.println("SocialMedia account deleted.");
    }
}
