package com.xworks.Inheritance.external;

public class Software {
    public void bootUp() {
        System.out.println("Software is booting up...");
    }

    public void manageMemory() {
        System.out.println("Software is managing memory...");
    }

    public void handleProcesses() {
        System.out.println("Software is handling processes...");
    }

    public void executePrograms() {
        System.out.println("Software is executing programs...");
    }

    public void shutDown() {
        System.out.println("Software is shutting down...");
    }
}
