package com.xworks.Inheritance.external;

public class SoftwareTool {
    public void openImage() {
        System.out.println("SoftwareTool: Opening an image...");
    }

    public void applyFilter() {
        System.out.println("SoftwareTool: Applying filter...");
    }

    public void cropImage() {
        System.out.println("SoftwareTool: Cropping image...");
    }

    public void adjustBrightness() {
        System.out.println("SoftwareTool: Adjusting brightness...");
    }

    public void saveImage() {
        System.out.println("SoftwareTool: Saving image...");
    }
}
