package com.xworks.Inheritance.external;

public class SpaceObject {
    public void rotate() {
        System.out.println("SpaceObject is rotating");
    }

    public void revolve() {
        System.out.println("SpaceObject is revolving");
    }

    public void haveGravity() {
        System.out.println("SpaceObject has gravity");
    }

    public void supportLife() {
        System.out.println("SpaceObject may or may not support life");
    }

    public void orbitSun() {
        System.out.println("SpaceObject orbits a star");
    }
}
