package com.xworks.Inheritance.external;

public class Storage {
    public void storeData() {
        System.out.println("Storage is storing data.");
    }

    public void retrieveData() {
        System.out.println("Storage is retrieving data.");
    }

    public void format() {
        System.out.println("Storage is formatting.");
    }

    public void connect() {
        System.out.println("Storage is connecting to the system.");
    }

    public void disconnect() {
        System.out.println("Storage is disconnecting from the system.");
    }
}

