package com.xworks.Inheritance.external;

public class Student {
    public void study() {
        System.out.println("Student is studying");
    }

    public void attendClasses() {
        System.out.println("Student is attending classes");
    }

    public void submitAssignments() {
        System.out.println("Student is submitting assignments");
    }

    public void takeExams() {
        System.out.println("Student is taking exams");
    }

    public void graduate() {
        System.out.println("Student is graduating");
    }
}
