package com.xworks.Inheritance.external;

public class Sword {
    public void swing() {
        System.out.println("Sword is swung.");
    }

    public void stab() {
        System.out.println("Sword is stabbed.");
    }

    public void block() {
        System.out.println("Sword is blocking an attack.");
    }

    public void sharpen() {
        System.out.println("Sword is being sharpened.");
    }

    public void attack() {
        System.out.println("Sword is attacking.");
    }
}
