package com.xworks.Inheritance.external;

public class Tool {
    public void use() {
        System.out.println("Tool is being used.");
    }

    public void maintain() {
        System.out.println("Tool is being maintained.");
    }

    public void carry() {
        System.out.println("Tool is being carried.");
    }

    public void store() {
        System.out.println("Tool is being stored.");
    }

    public void repair() {
        System.out.println("Tool is being repaired.");
    }
}
