package com.xworks.Inheritance.external;

public class Toy {
    public void hug() {
        System.out.println("Toy is being hugged.");
    }

    public void squeeze() {
        System.out.println("Toy is being squeezed.");
    }

    public void wash() {
        System.out.println("Toy is being washed.");
    }

    public void sitUpright() {
        System.out.println("Toy is sitting upright.");
    }

    public void decorateRoom() {
        System.out.println("Toy is decorating the room.");
    }
}
