package com.xworks.Inheritance.external;

public class Train {
    public void startJourney() {
        System.out.println("Train is starting its journey.");
    }

    public void stopAtStation() {
        System.out.println("Train is stopping at a station.");
    }

    public void accelerate() {
        System.out.println("Train is accelerating.");
    }

    public void decelerate() {
        System.out.println("Train is decelerating.");
    }

    public void announceArrival() {
        System.out.println("Train is announcing arrival at the station.");
    }
}
