package com.xworks.Inheritance.external;

public class Tree {
    public void growLeaves() {
        System.out.println("Tree is growing leaves.");
    }

    public void provideShade() {
        System.out.println("Tree provides shade.");
    }

    public void bearFruit() {
        System.out.println("Tree is bearing fruit.");
    }

    public void absorbCO2() {
        System.out.println("Tree is absorbing CO2.");
    }

    public void supportWildlife() {
        System.out.println("Tree is supporting wildlife.");
    }
}
